mapper.select:
select max(prono) as prono
from resumetb
where puserid = #{puserid}

SELECT prono, protitle
FROM resumetb
WHERE puserid = #{puserid}

mapper.update:
UPDATE resumetb
SET representative = false
WHERE puserid = #{puserid}

UPDATE resumetb
SET representative = true
WHERE puserid = #{puserid} AND prono = #{prono}

mapper.select:
select puserid, prono
from resumetb
where puserid = #{puserid}
